CREATE PROCEDURE [Demo].[spCompanyCreate]
   @CompanyId AS UNIQUEIDENTIFIER = NULL OUTPUT
  ,@Name AS NVARCHAR(100) NULL = NULL
  ,@Address AS NVARCHAR(250) NULL = NULL
  ,@Phone AS NVARCHAR(50) NULL = NULL
  ,@CreatedBy AS NVARCHAR(250) NULL = NULL
  ,@CreatedDate AS DATETIME2 NULL = NULL
  ,@ReselectRecord AS BIT = 0
AS
BEGIN
  /*
   * This file is automatically generated; any changes will be lost. 
   */
 
  SET NOCOUNT ON;
  
  BEGIN TRY
    -- Wrap in a transaction.
    BEGIN TRANSACTION

    -- Set audit details.
    EXEC @CreatedDate = fnGetTimestamp @CreatedDate
    EXEC @CreatedBy = fnGetUsername @CreatedBy

    DECLARE @InsertedIdentity TABLE([CompanyId] UNIQUEIDENTIFIER)

    -- Create the record.
    INSERT INTO [Demo].[Company] (
        [Name]
       ,[Address]
       ,[Phone]
       ,[CreatedBy]
       ,[CreatedDate]
    )
    OUTPUT inserted.CompanyId INTO @InsertedIdentity
    VALUES (
        @Name
       ,@Address
       ,@Phone
       ,@CreatedBy
       ,@CreatedDate
    )

    -- Get the inserted identity.
    SELECT @CompanyId = [CompanyId] FROM @InsertedIdentity

    -- Commit the transaction.
    COMMIT TRANSACTION
  END TRY
  BEGIN CATCH
    -- Rollback transaction and rethrow error.
    IF @@TRANCOUNT > 0
      ROLLBACK TRANSACTION;

    THROW;
  END CATCH
  
  -- Reselect record.
  IF @ReselectRecord = 1
  BEGIN
    EXEC [Demo].[spCompanyGet] @CompanyId
  END
END