CREATE PROCEDURE [Demo].[spCompanyUpdate]
   @CompanyId AS UNIQUEIDENTIFIER
  ,@Name AS NVARCHAR(100) NULL = NULL
  ,@Address AS NVARCHAR(250) NULL = NULL
  ,@Phone AS NVARCHAR(50) NULL = NULL
  ,@RowVersion AS TIMESTAMP
  ,@UpdatedBy AS NVARCHAR(250) NULL = NULL
  ,@UpdatedDate AS DATETIME2 NULL = NULL
  ,@ReselectRecord AS BIT = 0
AS
BEGIN
  /*
   * This file is automatically generated; any changes will be lost. 
   */
 
  SET NOCOUNT ON;
  
  BEGIN TRY
    -- Wrap in a transaction.
    BEGIN TRANSACTION

    -- Set audit details, etc.
    EXEC @UpdatedDate = fnGetTimestamp @UpdatedDate
    EXEC @UpdatedBy = fnGetUsername @UpdatedBy

    -- Check exists.
    DECLARE @PrevRowVersion BINARY(8)
    SET @PrevRowVersion = (SELECT TOP 1 x.[RowVersion] FROM [Demo].[Company] AS x WHERE x.[CompanyId] = @CompanyId)
    IF @PrevRowVersion IS NULL
    BEGIN
      EXEC spThrowNotFoundException
    END

    -- Check concurrency (where provided).
    IF @RowVersion IS NULL OR @PrevRowVersion <> @RowVersion
    BEGIN
      EXEC spThrowConcurrencyException
    END

    -- Update the record.
    UPDATE [Demo].[Company] SET
         [Name] = @Name
        ,[Address] = @Address
        ,[Phone] = @Phone
        ,[UpdatedBy] = @UpdatedBy
        ,[UpdatedDate] = @UpdatedDate
      WHERE [CompanyId] = @CompanyId

    -- Commit the transaction.
    COMMIT TRANSACTION
  END TRY
  BEGIN CATCH
    -- Rollback transaction and rethrow error.
    IF @@TRANCOUNT > 0
      ROLLBACK TRANSACTION;

    THROW;
  END CATCH
  
  -- Reselect record.
  IF @ReselectRecord = 1
  BEGIN
    EXEC [Demo].[spCompanyGet] @CompanyId
  END
END